// INFINITX AI Placeholders Generator
// ==============================================
// This code extracts all relevant data from GoHighLevel and formats it
// into a clean, accessible object for use with AI agents.
// It dynamically handles custom fields, calendar detection, and conversation history.

// Get input data from nodes (all pre-formatted as strings)
const customValuesData = $('Get Custom Values GHL').item.json.custom_values || "{}";
const customFieldsData = $('Get Custom Fields GHL').item.json.custom_fields || "{}";
const contactData = $('Get Contact GHL').item.json.contact_details || "{}";
const calendarData = $('Get Calendars GHL').item.json.calendars || "{}";
const conversationsData = $('Search Conversations GHL').item.json || "{}";
const messagesOutput = $('Get Messages By Conversation ID GHL').item.json || "{}";
const notesOutput = $('Get Notes GHL').item.json || "{}";
const formattedNotes = $('Code Parse Notes').item.json?.readableNotes || "";
const formattedConversations = $('Code Parse Notes').item.json?.readableConversations || "";

// Parse the JSON strings to objects
const customValuesObj = JSON.parse(customValuesData);
const customFieldsObj = JSON.parse(customFieldsData);
const contactObj = JSON.parse(contactData);
const calendarObj = JSON.parse(calendarData);

// Extract the contact data
const contact = contactObj.contact || {};

// Extract custom fields array from both sources
const accountCustomFields = customFieldsObj.customFields || [];
const contactCustomFields = contact.customFields || [];
const accountCustomValues = customValuesObj.customValues || [];
const calendars = calendarObj.calendars || [];

// Create a mapping of field IDs and field keys to field names and snake_case names
const fieldMap = {};
accountCustomFields.forEach(field => {
  // Convert field name to snake_case for variable names
  const snakeCaseName = field.name
    .toLowerCase()
    .replace(/[^\w\s]/g, '')
    .replace(/\s+/g, '_');

  // If field.fieldKey starts with "contact." remove that prefix
  let fieldKey = field.fieldKey || "";
  if (fieldKey.startsWith("contact.")) {
    fieldKey = fieldKey.substring("contact.".length);
  }

  fieldMap[field.id] = {
    name: field.name,
    fieldKey: fieldKey,
    snakeCaseName: snakeCaseName
  };

  // Also index by fieldKey for easy lookup if it exists
  if (fieldKey) {
    fieldMap[fieldKey] = {
      name: field.name,
      id: field.id,
      snakeCaseName: snakeCaseName
    };
  }
});

// Create an object to store contact field values
const contactFieldValues = {};

// Populate values from contact custom fields
contactCustomFields.forEach(field => {
  if (fieldMap[field.id]) {
    // Convert values to strings
    contactFieldValues[fieldMap[field.id].snakeCaseName] = String(field.value || "");
  }
});

// Format contact name with proper capitalization
const firstName = contact.firstNameLowerCase ? 
  contact.firstNameLowerCase.charAt(0).toUpperCase() + contact.firstNameLowerCase.slice(1) : 
  '';
const lastName = contact.lastNameLowerCase ? 
  contact.lastNameLowerCase.charAt(0).toUpperCase() + contact.lastNameLowerCase.slice(1) : 
  '';
const fullName = `${firstName} ${lastName}`.trim() || 'none';

// Create a complete mapping of all calendars (no hardcoded names)
const calendarMap = {};
calendars.forEach(calendar => {
  // Store by both original name and snake_case name for flexibility
  const calendarName = calendar.name || "";
  const snakeCaseName = calendarName
    .toLowerCase()
    .replace(/[^\w\s]/g, '')
    .replace(/\s+/g, '_');

  calendarMap[calendarName] = calendar.id;
  calendarMap[snakeCaseName] = calendar.id;
});

// Dynamically detect and categorize calendars based on naming patterns
// This is a flexible approach that doesn't rely on hardcoded calendar names
const calendarTypes = {
  sales: ['sales', 'discovery', 'consultation'],
  onboarding: ['onboarding', 'welcome', 'orientation'],
  customer_success: ['success', 'support', 'check-in', 'checkin', 'follow-up', 'followup'],
  referral: ['referral', 'partner']
};

// Initialize the calendar types object
const detectedCalendarTypes = {};

// Map each calendar to a type based on name patterns
Object.entries(calendarMap).forEach(([calendarName, calendarId]) => {
  // Skip snake_case duplicates
  if (calendarName.includes('_')) return;

  const lowerName = calendarName.toLowerCase();

  // Try to categorize this calendar
  for (const [type, keywords] of Object.entries(calendarTypes)) {
    if (keywords.some(keyword => lowerName.includes(keyword))) {
      // First match for this type becomes the primary calendar for that type
      if (!detectedCalendarTypes[type]) {
        detectedCalendarTypes[type] = String(calendarId);
      }
    }
  }
});

// Create placeholders object with separate sections for different types of data
const placeholders = {
  // ===========================================
  // CONTACT INFO
  // Basic information about the contact
  // ===========================================
  contact: {
    first_name: String(firstName || 'none'),
    full_name: String(fullName),
    email: String(contact.email || 'none'),
    phone: String(contact.phone || 'none'),
    address: String(contact.address || 'none'),
    business_name: String(contact.businessName || contact.companyName || 'none'),
    contact_id: String(contact.id || ''),
    tags: Array.isArray(contact.tags) ? contact.tags.map(tag => String(tag)) : []
  },

  // ===========================================
  // CUSTOM FIELDS
  // Field IDs and values from the contact's profile
  // ===========================================
  // Custom field IDs (for dynamic field updates)
  custom_field_ids: {},

  // Custom field keys (for easier field updates)
  custom_field_keys: {},

  // Custom field values from the contact
  custom_field_values: contactFieldValues,

  // ===========================================
  // ACCOUNT VALUES
  // Custom values from the account settings
  // ===========================================
  custom_values: {},

  // ===========================================
  // CALENDARS
  // Available calendars and their IDs
  // ===========================================
  // All calendars with their IDs
  calendars: calendarMap,

  // Detected calendar types
  calendar_types: detectedCalendarTypes,

  // ===========================================
  // CONVERSATION HISTORY
  // Previous messages and notes
  // ===========================================
  history: {
    conversations: formattedConversations,
    notes: formattedNotes
  }
};

// Add all custom field IDs and keys to the placeholders
accountCustomFields.forEach(field => {
  const snakeCaseName = fieldMap[field.id].snakeCaseName;

  // Add field ID with snake_case format
  placeholders.custom_field_ids[snakeCaseName] = String(field.id);

  // For field keys, use the cleaned key (without contact. prefix) if available
  // Otherwise, use the snake_case name as a fallback
  let fieldKey = fieldMap[field.id].fieldKey;
  placeholders.custom_field_keys[snakeCaseName] = fieldKey || snakeCaseName;
});

// Add all custom values to the placeholders
accountCustomValues.forEach(customValue => {
  const name = customValue.name;
  // Convert value name to snake_case for variable names
  const snakeCaseName = name
    .toLowerCase()
    .replace(/[^\w\s]/g, '')
    .replace(/\s+/g, '_');

  placeholders.custom_values[snakeCaseName] = String(customValue.value || "");
});

// Add convenience shortcuts for common calendar types
// These will be undefined if no matching calendar was found
placeholders.sales_calendar_id = detectedCalendarTypes.sales;
placeholders.sales2_calendar_id = detectedCalendarTypes.referral || detectedCalendarTypes.sales;
placeholders.onboarding_calendar_id = detectedCalendarTypes.onboarding;
placeholders.customer_success_calendar_id = detectedCalendarTypes.customer_success;

// Return the complete placeholders object
return { placeholders };

