// This function will run once per item and returns the new item
function processItem(item) {
  try {
    // Directly reference the Search Conversations GHL1 node to get the tags
    const tags = $('Search Conversations GHL1').item.json.conversations[0].tags;

    // If no tags, return a default value
    if (!tags || tags.length === 0) {
      return {
        json: {
          leadDisposition: "No tag found"
        }
      };
    }

    // Define our possible lead disposition tags for both text and email formats
    const leadTags = [
      // Text format tags
      "hot lead (text)",
      "warm lead (text)",
      "scheduled (text)",
      "not interested (text)",
      // Email format tags
      "hot lead (email)",
      "warm lead (email)",
      "scheduled (email)",
      "not interested (email)"
    ];

    // Find the first matching lead tag
    const foundTag = tags.find(tag => leadTags.includes(tag));

    // If no matching tag was found
    if (!foundTag) {
      return {
        json: {
          leadDisposition: "No lead disposition tag found"
        }
      };
    }

    // Extract the actual disposition by removing both " (text)" and " (email)" from the end
    const leadDisposition = foundTag
      .replace(" (text)", "")
      .replace(" (email)", "");

    // Return the processed item with just the lead disposition string
    return {
      json: {
        leadDisposition: leadDisposition
      }
    };
  } catch (error) {
    // Handle any errors gracefully
    return {
      json: {
        error: `Error processing tags: ${error.message}`,
        leadDisposition: "Error"
      }
    };
  }
}

// Return the processed item
return processItem($input);

